mapper.select:
select

  distinct


from product




  order by ${orderByClause}



    limit ${offset}, ${limit}


    limit ${limit}

select

  distinct

product.product_id, product.product_no, product.product_name, product.unit, product.category_id,
product.spec, product.price, product.closed, product.remark, product.create_at, product.create_by, product.update_at,
product.update_by, product_category.category_name as category_name
from product
left join product_category on product.category_id = product_category.category_id




  order by ${orderByClause}



    limit ${offset}, ${limit}


    limit ${limit}

SELECT product.*, IFNULL((total- product.ordered_amount), 0) AS left_amount , IFNULL(b.total, 0) AS total_amount FROM ((SELECT a.product_id, sum(a.quantity) AS total FROM ( SELECT product_id, -quantity AS quantity FROM product_outstock_bill LEFT JOIN product_outstock_bill_product ON product_outstock_bill.bill_id = product_outstock_bill_product.bill_id WHERE bill_state = 3 UNION ALL SELECT product_id, quantity FROM product_instock_bill LEFT JOIN product_instock_bill_product ON product_instock_bill.bill_id = product_instock_bill_product.bill_id WHERE bill_state = 3 ) AS a GROUP BY product_id) AS b RIGHT JOIN ( SELECT product.*, IFNULL(c.ordered, 0) AS ordered_amount FROM product LEFT JOIN ( SELECT product_id, sum(quantity) AS ordered FROM product_outstock_bill LEFT JOIN product_outstock_bill_product ON product_outstock_bill.bill_id = product_outstock_bill_product.bill_id GROUP BY product_id) AS c ON c.product_id = product.product_id) AS product ON product.product_id = b.product_id)
order by ${orderByClause}
limit ${offset}, ${limit}
limit ${limit}

SELECT IFNULL(SUM(IFNULL((total- product.ordered_amount), 0)), 0) AS left_amount , IFNULL(SUM(IFNULL(b.total, 0)), 0) AS total_amount FROM ((SELECT a.product_id, sum(a.quantity) AS total FROM ( SELECT product_id, -quantity AS quantity FROM product_outstock_bill LEFT JOIN product_outstock_bill_product ON product_outstock_bill.bill_id = product_outstock_bill_product.bill_id WHERE bill_state = 3 UNION ALL SELECT product_id, quantity FROM product_instock_bill LEFT JOIN product_instock_bill_product ON product_instock_bill.bill_id = product_instock_bill_product.bill_id WHERE bill_state = 3 ) AS a GROUP BY product_id) AS b RIGHT JOIN ( SELECT product.*, IFNULL(c.ordered, 0) AS ordered_amount FROM product LEFT JOIN ( SELECT product_id, sum(quantity) AS ordered FROM product_outstock_bill LEFT JOIN product_outstock_bill_product ON product_outstock_bill.bill_id = product_outstock_bill_product.bill_id GROUP BY product_id) AS c ON c.product_id = product.product_id) AS product ON product.product_id = b.product_id)
order by ${orderByClause}
limit ${offset}, ${limit}
limit ${limit}
